import React from 'react'
import ReactDOM from 'react-dom';

import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";

import './Semantic/semantic.min.css'
import './style.css'
import {Button, Modal} from 'semantic-ui-react'
import Register from './RegisterForm'
import Main_login from "./main_login";
import AuthService from "../services/auth_service";


function Load_main_login(){
    ReactDOM.render(<Main_login/>, document.getElementById("root"))
}

function Register_open(){
    ReactDOM.render(<Register />, document.getElementById("main_root"))
}


export default class Login extends Component {
    constructor(props) {
        super(props);
        this.handleLogin = this.handleLogin.bind(this);
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onChangeOpen1 = this.onChangeOpen1.bind(this);
        this.onChangeSecondOpen = this.onChangeSecondOpen.bind(this);
        // const [open1, setOpen] = React.useState(false)
        // const [secondOpen, setSecondOpen] = React.useState(false)

        this.state = {
            username: "",
            password: "",
            loading: false,
            message: "",
            open1: false,
            secondOpen: false
        };
    }

    onChangeOpen1(e) {
        this.setState({
            open1: e
        })
    }

    onChangeSecondOpen(e) {
        this.setState({
            secondOpen: e
        })
    }

    onChangeUsername(e) {
        this.setState({
            username: e.target.value
        });
    }

    onChangePassword(e) {
        this.setState({
            password: e.target.value
        });
    }

    handleLogin(e) {
        e.preventDefault();

        this.setState({
            message: "",
            loading: true
        });

        this.form.validateAll();

        if (this.checkBtn.context._errors.length === 0) {
            AuthService.login(this.state.username, this.state.password).then(
                () => {
                    this.props.history.push("/profile");
                    window.location.reload();
                },
                error => {
                    const resMessage =
                        (error.response &&
                            error.response.data &&
                            error.response.data.message) ||
                        error.message ||
                        error.toString();

                    this.setState({
                        loading: false,
                        message: resMessage
                    });
                }
            );
        } else {
            this.setState({
                loading: false
            });
        }
    }
    render(){
        return (
            <>
                <Modal
                    onClose= {this.onChangeOpen1(false)}
                    onOpen={this.onChangeSecondOpen(true)}
                    open={this.state.open1}
                    trigger={<a href='#' className='black'>
                        <i className="black user circle outline icon"></i><span className='black color_text'>Login</span>
                    </a>}
                >
                    <Modal.Content>
                        <Modal.Description>
                            <div className='ui container'>
                                <div className="ui placeholder segment">
                                    <div className="ui two column very relaxed stackable grid">
                                        <div className="column">
                                            <form className="ui form">
                                                <div className="field">
                                                    <label>Username</label>
                                                    <div className="ui left icon input">
                                                        <input type="text" placeholder="Username"/>
                                                        <i className="user icon"></i>
                                                    </div>
                                                </div>
                                                <div className="field">
                                                    <label>Password</label>
                                                    <div className="ui left icon input">
                                                        <input type="password"/>
                                                        <i className="lock icon"></i>
                                                    </div>
                                                </div>

                                            </form>
                                            <button className="ui big blue submit button" onClick={Load_main_login}>Login
                                            </button>
                                        </div>
                                        <div className="middle aligned column">
                                            <button className='ui big button' onClick={this.onChangeSecondOpen(true)} primary>
                                                <i className="signup icon"></i>
                                                Sign Up
                                            </button>

                                        </div>
                                    </div>
                                    <div className="ui vertical divider">
                                        Or
                                    </div>
                                </div>
                            </div>
                        </Modal.Description>
                    </Modal.Content>
                    <Modal.Actions>
                        <div className='ui grid'>
                            <div className='row'>
                                <div className='center aligned  column'>
                                    <h4> Wellcome HR Lotus</h4>
                                </div>
                            </div>

                        </div>

                    </Modal.Actions>
                </Modal>
                <Modal
                    onClose={this.onChangeSecondOpen(false)}
                    open={this.state.secondOpen}
                    size='small'
                >
                    <Modal.Content>
                        <Register/>
                    </Modal.Content>
                    <Modal.Actions>
                        <Button
                            icon='check'
                            content='All Done'
                            onClick={this.onChangeSecondOpen(false)}
                        />
                    </Modal.Actions>
                </Modal>
            </>
        );
    }
}
