import logo from './components/BK-plant-logo.png';
import React from 'react';
import ReactDOM from 'react-dom';
import './Style/Semantic/semantic.css'
import {render} from "@testing-library/react";

function turn_on_search() {
    var mini_search_bar = document.getElementById("mini_search_bar");
    mini_search_bar.style.display = 'none';

    var wide_search_bar = document.getElementById("wide_search_bar");
    wide_search_bar.style.display = 'inline-block'
}

function turn_off_search() {
    var wide_search_bar = document.getElementById("wide_search_bar");
    wide_search_bar.style.display = 'none';

    var mini_search_bar = document.getElementById("mini_search_bar");
    mini_search_bar.style.display = 'inline-block';
}

function App() {
    return (
        <div className='ui' >
            <div className="ui huge top menu">
                <div className='item'>
                    <img src={logo}/>
                </div>
                <a className='item'>Home</a>
                <a className='item'>Resource</a>
                <a className='item'>About Us</a>
                <div className='right menu'  >
                    <div className='item' style={{display:"none"}} id="wide_search_bar">
                        <div className="ui search">
                            <div className="ui icon input">
                                <input className="prompt" type='text'></input>
                            </div>
                            <a href='#' onClick={turn_off_search}>
                                <i className="search icon"  ></i>
                            </a>
                            <div className="results"></div>
                        </div>
                    </div>
                    <a className='item' href='#' id='mini_search_bar' onClick={turn_on_search}><i className='search icon'></i></a>
                    <a className='item'><i className='user circle outline icon'></i></a>
                </div>
            </div>
            <div className='row'>
                <div className="ui cube shape">
                    <div className="sides">
                        <div className="active side">
                            <div className="content">
                                <div className="center">
                                    1
                                </div>
                            </div>
                        </div>
                        <div className="side">
                            <div className="content">
                                <div className="center">
                                    2
                                </div>
                            </div>
                        </div>
                        <div className="side">
                            <div className="content">
                                <div className="center">
                                    3
                                </div>
                            </div>
                        </div>
                        <script>
                             var side_bar = document.getElementsByClassName('.shape');
                             side_bade.shape('flip up');
                        </script>

                    </div>
                </div>
            </div>
        </div>

    );
}

